$(function(){
    $(".page-wrapper").attr("id","layout");
    $(".navigation").attr("id","menu");
    $("header").addClass("header");
    $(".main").addClass("content");
    $(".navigation").addClass("pure-menu").addClass("pure-menu-open");
    $(".navigation .selected").addClass("pure-menu-selected");
    var c=document.getElementById("layout"),d=document.getElementById("menu"),b=document.getElementById("menuLink");
    function a(g,h){
        var f=g.className.split(/\s+/),j=f.length,e=0;
        for(;e<j;e++){
            if(f[e]===h){
                f.splice(e,1);
                break
            }
        }
        if(j===f.length){
            f.push(h)
        }
        g.className=f.join(" ")
    }
    b.onclick=function(g){
        var f="active";
        g.preventDefault();
        a(c,f);
        a(d,f);
        a(b,f)
    }
});
